CREATE  FUNCTION GetDaysInYear
(
@Date DATETIME 
)
RETURNS INT
AS
BEGIN

    DECLARE @IsLeapYear        BIT

    SET @IsLeapYear = 0
    IF (YEAR( @Date ) % 4 = 0 AND YEAR( @Date ) % 100 != 0) OR
        YEAR( @Date ) % 400 = 0
        SET @IsLeapYear = 1

    RETURN 365 + @IsLeapYear

END









































