	@SourceCode [nvarchar](30),
	@PrevPeriodStartDate [smalldatetime],
	@PrevPeriodEndDate [smalldatetime],
	@PeriodStartDate [smalldatetime],
	@PeriodEndDate [smalldatetime],
	@YearStartDate [smalldatetime],
	@YearEndDate [smalldatetime]
AS
DECLARE @PrevPeriodValue AS numeric(18,2)
DECLARE @PrevPeriodCount AS numeric(18,2)
DECLARE @PeriodValue AS numeric(18,2)
DECLARE @PeriodCount AS numeric(18,2)
DECLARE @TotalValue AS numeric(18,2)
