IF @SourceCode IS NOT NULL
    BEGIN
        SELECT @PrevPeriodValue = isnull(SUM(SubTotalRate), 0), @PrevPeriodCount =  isnull(COUNT(SubTotalRate), 0) FROM CustomerSalesOrder WHERE SourceCode = @SourceCode AND DateCreated >= @PrevPeriodStartDate AND DateCreated < DATEADD(day, 1, @PrevPeriodEndDate) AND IsVoided = 0 and MerchantOrderID_DEV000221 Is Not null

        SELECT @PeriodValue = isnull(SUM(SubTotalRate), 0), @PeriodCount =  isnull(COUNT(SubTotalRate), 0) FROM CustomerSalesOrder WHERE SourceCode = @SourceCode AND DateCreated >= @PeriodStartDate AND DateCreated < DATEADD(day, 1, @PeriodEndDate) AND IsVoided = 0 and MerchantOrderID_DEV000221 Is Not null

        SELECT @TotalValue = isnull(SUM(SubTotalRate), 0), @TotalCount =  isnull(COUNT(SubTotalRate), 0) FROM CustomerSalesOrder WHERE SourceCode = @SourceCode AND DateCreated >= @YearStartDate AND DateCreated < DATEADD(day, 1, @YearEndDate) AND IsVoided = 0 and MerchantOrderID_DEV000221 Is Not null
    END
ELSE

    BEGIN
        SELECT @PrevPeriodValue = isnull(SUM(SubTotalRate), 0), @PrevPeriodCount =  isnull(COUNT(SubTotalRate), 0) FROM CustomerSalesOrder WHERE DateCreated >= @PrevPeriodStartDate AND DateCreated < DATEADD(day, 1, @PrevPeriodEndDate) AND IsVoided = 0 and MerchantOrderID_DEV000221 Is Not null

