        SELECT @TotalValue = isnull(SUM(SubTotalRate), 0), @TotalCount =  isnull(COUNT(SubTotalRate), 0) FROM CustomerSalesOrder WHERE DateCreated >= @YearStartDate AND DateCreated < DATEADD(day, 1, @YearEndDate) AND IsVoided = 0 and MerchantOrderID_DEV000221 Is Not null
    END

SELECT @SourceCode AS SourceCode, @PrevPeriodStartDate AS PrevPeriodStartDate, @PrevPeriodEndDate AS PrevPeriodEndDate, 
	@PrevPeriodValue AS PrevPeriodValue, @PrevPeriodCount AS PrevPeriodCount, @PeriodStartDate AS PeriodStartDate, 
	@PeriodEndDate AS PeriodEndDate, @PeriodValue AS PeriodValue, @PeriodCount AS PeriodCount, @YearStartDate AS 
	YearStartDate, @YearEndDate AS YearEndDate, @TotalValue AS TotalValue, @TotalCount AS TotalCount FROM System_DEV000221
















